mapper.select:
select , case when r.status in (1,3,5,6) then r.end_time when r.status in (0,2,4) then NOW() end as end_time, t.job_name, a.name as agent_name, a.host, t.cron_exp, u.user_name from t_record as r inner join t_agent a on r.agent_id = a.agent_id inner join t_user as u on r.user_id = u.user_id
inner join t_job as t
left join t_job as t
on r.job_id = t.job_id where r.parent_id is null
and r.status in (1,3,4,5,6)
and r.status in (0,2,4)
and r.success = #{pager.filter.record.success}
and r.agent_id = #{pager.filter.record.agentId}
and r.job_id = #{pager.filter.record.jobId}
and r.exec_type = #{pager.filter.record.execType}
and t.job_name like '${pager.filter.record.jobName}'
and date_format(r.start_time,'%Y-%m-%d') = #{pager.filter.queryDate}
and r.user_id = #{pager.filter.userId}
order by r.start_time desc limit #{pager.offset},#{pager.pageSize}

select count(1)
from  t_record as r

    inner join t_job as t


    left join t_job as t

on  r.job_id = t.job_id
where r.parent_id is null

    and r.status in (1,3,4,5,6)



    and r.status in (0,2,4)




        and r.success = #{filter.record.success}



        and r.agent_id = #{filter.record.agentId}



        and r.job_id = #{filter.record.jobId}



        and r.exec_type = #{filter.record.execType}



        and t.job_name like '%${filter.record.jobName}%'




    and date_format(r.start_time,'%Y-%m-%d') = #{filter.queryDate}



    and r.user_id = #{filter.userId}

select record_id,message
from t_record_message
where record_id = #{recordId}

select date_format(r.start_time,'%Y-%m-%d') as `date`, sum(case r.success when 0 then 1 else 0 end) failed, sum(case r.success when 1 then 1 else 0 end) success, sum(case r.success when 2 then 1 else 0 end) killed, sum(case r.success when 3 then 1 else 0 end) timeout, sum(case r.success when 4 then 1 else 0 end) lost, sum(case r.job_type when 0 then 1 else 0 end) singleton, sum(case r.job_type when 1 then 1 else 0 end) flow, sum(case j.cron_type when 0 then 1 else 0 end) crontab, sum(case j.cron_type when 1 then 1 else 0 end) quartz, sum(case r.exec_type when 0 then 1 else 0 end) auto, sum(case r.exec_type when 1 then 1 else 0 end) operator from t_record r left join t_job j on r.job_id=j.job_id where date_format(r.start_time,'%Y-%m-%d') between #{filter.start} and #{filter.end}
and r.user_id = #{filter.userId}
group by date_format(r.start_time,'%Y-%m-%d') order by date_format(r.start_time,'%Y-%m-%d') asc

